/* Custom color in header. */
.skin-blue .main-header .navbar {
	background-color: ###colour_custom_skin### !important;
}

.skin-blue .main-header .logo {
  background-color: ###colour_custom_skin### !important;
}

/* Custom color in sidebar accent on hovering over tabItem. */
.skin-blue .sidebar-menu > li.active > a, .skin-blue .sidebar-menu > li:hover > a {
  border-left-color: ###colour_custom_skin### !important;
}

/* Custom color in top of box. */
.box {
  border-top: 3px solid ###colour_custom_skin###;
}

# Source functions.
function_files = list.files(path='functions', full.names=TRUE, recursive=TRUE)

for (file in function_files) {
  source(file, encoding='UTF-8')
}

# Source modules.
module_files = list.files(path='modules', full.names=TRUE, recursive=TRUE)

for (file in module_files) {
  source(file, encoding='UTF-8')
}

# Load data.
data_files = list.files(path='../data/*.RData', full.names=TRUE, recursive=TRUE)

for (file in data_files) {
  load(file)
}

